    public static ArrayList<ModelMuseum> ambilDataMuseum() {
        ArrayList<ModelMuseum> dataMuseum = new ArrayList<>();
        for (String[] varData : data) {
            ModelMuseum model = new ModelMuseum();
            model.setNama(varData[0]);
            model.setTentang(varData[1]);
            model.setFoto(varData[2]);

            dataMuseum.add(model);
        }

        return dataMuseum;
    }
